import UIKit

struct Post {
    let author: String
    let image: UIImage
    var description: String
    var likes: Int
    var views: Int
    
    static func makePost() -> [Post] {
    
        var post = [Post]()
        
        post.append(Post(author: "Star Wars", image: UIImage(named: "Mandalorian")!, description: "Гриф Карга уговаривает Дина Джарина в первом отрывке из третьего сезона «Мандалорца». В промо-кампанию третьего сезона «Мандалорца» вступают непосредственные отрывки из свежих эпизодов. В сети появился первый фрагмент из продолжения, где Гриф Карга (Карл Уэзерс) пытается уговорить Дина Джарина (Педро Паскаль) остаться и принять новый заказ, но у главного героя другие планы. Мандалорец продолжит свой непростой путь, который рано или поздно приведёт его на родину. В путешествиях Дина Джарина будет сопровождать не только Грогу. Трейлеры и промо-кадры прозрачно намекали, что вместе с ним какое-то время попутешествует Бо-Катан Крайз (Кэти Сакхофф).", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Star Wars", image: UIImage(named: "Andor")!, description: "Диего Луна и Стеллан Скарсгард засветились на съёмках второго сезона «Андора». Один из лучших сериалов прошлого года вернулся на производственные рельсы. В столице Великобритании не так давно стартовало активное производство второго сезона «Андора», и в сети уже появились фотографии со съёмочной площадки новых эпизодов. На снимках засветились ключевые персонажи истории — Кассиан Андор (Диего Луна) и Лютен Раэль (Стеллан Скарсгард).", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Киновселенная Марвел", image: UIImage(named: "TomHolland")!, description: "Том Холланд вернётся к роли Человека-паука в MCU. Информация о том, что Том Холланд не закончил с партией Питера Паркера после отгремевшего триквела «Нет пути домой», уже появлялась, но никакой конкретики вовлечённые в процесс принятия решений лица не сообщали. Нынче же кое-чем может поделиться сам Кевин Файги — глава Marvel Studios.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Fast&Furious", image: UIImage(named: "PaulWalker")!, description: "Режиссёр «Форсажа 10» рассказал о возвращении Пола Уокера. Постановщик «Форсажа 10» Луи Летерье продолжает делиться подробностями предстоящего фильма. Он уже рассказал, как буквально на коленке переписывал сценарий десятой части — видимо, в пользу большей реалистичности. Теперь дошла очередь и до Пола Уокера — чем ближе финал франчайза, тем острее встаёт вопрос его возможного возвращения. Как и ожидалось, оное не столько вероятно, сколько неизбежно — осталось договориться с семьёй актёра и сделать всё правильно. Но подходящий момент ещё не наступил — в «Форсаже 10» Брайан О’Коннор появится только во флешбэках.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Atomic Heart", image: UIImage(named: "AtomicHeart2")!, description: "Atomic Heart продолжает неумолимое, как победа коммунизма, шествие по планете. Отдельные граждане уже вдоль и поперёк прошли игру и, лениво позёвывая, интересуются, что там по поводу дополнительной порции советского ретрофутуризма и робототехники? Что ж, для изголодавшихся игроков есть хорошие новости. Блогер и журналист Алексей Макаренков, используя личные и профессиональные связи, пообщался с сотрудниками студии Mundfish и поделился полученными ответами. Первым делом разработчики сообщили, что сейчас трудятся над обещанными ранее DLC, коих будет четыре штуки. И речь, надо полагать, вовсе не о косметических украшениях, а о новых миссиях. Так это или нет — пока неясно, но студия готовит инфоповод, в рамках которого раскроет всякое интересное. Также стало известно, что Atomic Heart показала достойные результаты продаж. Точных цифр никто не назвал, но по имеющейся информации, даже в рамках платформы VK Play, на которой шутер эксклюзивно распространяется в России и странах СНГ, продажи уже отбили покупку прав с запасом. Надо полагать, и на других платформах дело обстоит аналогично. Но, опять же, никаких точных цифр — лишь голословные инсайдерские предположения. Ну и самое важное: Mundfish уже планирует Atomic Heart 2. Видимо, сказываются отличные продажи и хорошие отзывы. Правда, дело ещё на самой ранней стадии (если действительно ведётся), так что даже официального анонса придётся подождать. Как бы то ни было, такое развитие событий выглядит как минимум логичным.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Shazam 2", image: UIImage(named: "Shazam2")!, description: "Режиссёр «Шазама 2» Дэвид Ф. Сандберг призывает не смотреть трейлеры фильма, но уже поздно — мы всё видели. Так, новый телеролик сиквела раскрыл главную на данный момент интригу с возможным появлением Чудо-женщины в исполнении Галь Гадот. Спойлер, она там действительно будет — теперь точно. Сам ролик пока недоступен, однако кадры не оставляют места для сомнений — это ОНА.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Миссия невыполнима 8", image: UIImage(named: "MissionImpossible")!, description: "Автор четырёх из восьми невыполнимых миссий Тома Круза Кристофер Маккуорри накануне рассказал, что к касту фильма «Миссия невыполнима 8» присоединилась Ханна Уэддингхэм («Тед Лассо», «Игра престолов»), а также показал, как именно будет выглядеть её героиня. Кого сыграет Уэддингхэм, пока не уточняется, но в том, что это будет блестяще, уже можно не сомневаться.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Подземелье и драконы", image: UIImage(named: "DungeonAndDragons")!, description: "Разудалое приключенческое фэнтези с обилием юмора под названием «Подземелья и драконы: Воровская честь» явило себя зрителям из числа тех, кто принимает активное участие в мероприятиях на полях ежегодного фестиваля SXSW. И первые отзывы от посмотревших фильм по миру культовой игровой серии Dungeons & Dragons уже появились в сети. Если кратко, похождения развесёлой компашки во главе с Крисом Пайном пришлись аудитории по душе.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        post.append(Post(author: "Трансформеры 7", image: UIImage(named: "Transformers")!, description: "РВ Техасе проходит фестиваль SXSW, куда забросили десант блокбастера «Трансформеры: Восхождение звероботов». Его основные представители — статуи главных героев, одна скоростная тачка и творческий коллектив художественной картины. Сюжет рассказывает, как в 1994 году пара археологов из Бруклина (Доминик Фишбэк и Энтони Рамос) раскапывает древний конфликт между Автоботами и Десептиконами, куда влезли ещё три фракции расы Трансформеров: Максималы, Предаконы и Террорконы. Максималы — следующая ступень кибертронской эволюции, связанная с животным царством; Предаконы — их враждебная и хищная составляющая. Ну, а Террорконы — независимая группа с Кибертрона, желающая тотального уничтожения Автоботам и Максималам. Стивен Кейпл-младший срежиссировал кино, которое выйдет в начале июня 2023 года.", likes: Int.random(in: 300...500), views: Int.random(in: 700...1999)))
        
        return post
    }
}
